                        sonucLabel4.setText(String.valueOf(CONST.alerjiYuzde));
                        
                        //Yapılan Sonuçlarda Alerji mısın değil misin onu buluyor
                        diseaseDiagnosis.hastalikTani(CONST.alerjiYuzde);
                        
                        
                        //Pasta Grafiğin Değerlerinin Belirlenmesi
