    private void guncelleButonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_guncelleButonActionPerformed

           //Ekrandaki Değerleri Alıp Değişkenin İçine Atama
           String ad_soyad = yeniAdSoyadTextField.getText() ;
           Long tc = Long.parseLong(yeniTcNoTextField.getText());
           int cinsiyet = yeniCinsiyetComboBox.getSelectedIndex();
           int  yas = Integer.parseInt((String) yeniYasComboBox.getItemAt(yeniYasComboBox.getSelectedIndex()));
           int  kilo = Integer.parseInt((String) yeniKiloComboBox.getItemAt(yeniKiloComboBox.getSelectedIndex()));
           int  boy = Integer.parseInt((String) yeniBoyComboBox.getItemAt(yeniBoyComboBox.getSelectedIndex()));
           String eskiSifre = new String(eskiSifrePasswordField.getPassword());
           String yeniSifre = new String(yeniSifrePasswordField.getPassword());
           CONST.dosyaKonumu = CONST.dosyaKonumu.replaceAll("\\\\", "?");
        
        try {
                //Eski Şifrenin Doğru Girilip Girilmediğini Kontrol Etme
               if(eskiSifre.equals(CONST.results.getString("sifre"))){
                   
                 //Verilerin Güncellenmesi İçin Yazılan Sql Komutu
                 String sqlKomutu = "UPDATE bilgiler SET "
                         + "ad_soyad = '"+ad_soyad+"',"
                         + "tc_no = '"+tc+"',"
                         + "cinsiyet = '"+cinsiyet+"',"
                         + "yas = '"+yas+"',"
                         + "kilo = '"+kilo+"',"
                         + "boy = '"+boy+"',"
                         + "sifre = '"+yeniSifre+"',"
                         + "dosya_konumu = '"+CONST.dosyaKonumu+"' "
                         + "WHERE tc_no = "+CONST.userTcNo.toString();
                
                         //Yazılan Sql Komutun Çalıştırılması
                         registerFrame.baglantiAc();
                         Statement calistir = baglan.createStatement();
                         calistir.execute(sqlKomutu);
                         registerFrame.baglantiKapat();
                         CONST.userAdiSoyadi = ad_soyad;
                         CONST.userTcNo = tc;
                         CONST.userCinsiyet = cinsiyet;
                         CONST.userYas = yas;
                         CONST.userKilo = kilo;
                         CONST.userBoy = boy;
                         CONST.userSifre = yeniSifre;
                         JOptionPane.showMessageDialog(null,"Güncelleme Başarılı");
                }else{
                   JOptionPane.showMessageDialog(null, "Eski Şifreyi Eksik Yada Hatalı Girdiniz");
               }
           } catch (SQLException ex) {
               Logger.getLogger(mainFrame.class.getName()).log(Level.SEVERE, null, ex);
               JOptionPane.showMessageDialog(null, ex);
           }
    }//GEN-LAST:event_guncelleButonActionPerformed

